mapper.update:
UPDATE USER
SET BALANCE=#{BALANCE}
WHERE USERNAME = #{USERNAME}

mapper.select:
SELECT COUNT(*)
FROM USER_HAS_STARSHIP
WHERE USER_USERNAME = #{USERNAME}

mapper.update:
UPDATE USER
SET SEX            = #{SEX},
    BORN_TIME=#{BORN_TIME},
    SERVING_COUNTRY=#{SERVING_COUNTRY},
    BORN_COUNTRY=#{BORN_COUNTRY}
WHERE USERNAME = #{USERNAME}
